/* eslint-disable no-shadow */
/* eslint-disable no-alert */
/* eslint-disable react-native/no-inline-styles */
/* eslint-disable no-trailing-spaces */
/* eslint-disable no-unused-vars */
/* eslint-disable prettier/prettier */
import { StatusBar } from 'expo-status-bar';
import React,{Component} from 'react';
import { StyleSheet, Text, View } from 'react-native';
import Menu from './components/menuComponent';
import TextComponent from './components/textComponent';
import Navigation from './components/Navigation';
import {Header,Button} from 'react-native-elements';
import { TextInput } from '@shoutem/ui';
import SearchBar from './components/SearchBar';
import AppHeader from './components/Header';
import YTSearch from 'youtube-api-search';
const API_KEY = 'AIzaSyCP7dpnv3TV7TQe92PQQZsCdQIqN-GUHSk';
import VideoList from './components/VideoList';
import ReactYouTube from './components/YoutubeVideo';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
        text1:'',
        text2:'',
        selectedLabel:'',
        loading:false,
        videos:[],
    };
}

changeTextOne = (text1) => {
    this.setState({ text1: text1});
  }
changeTextTwo = (text2) => {
  this.setState({ text2: text2});
}  

Show = (value) => {
  this.setState({selectedLabel:value});
  //alert(this.state.selectedLabel);
 }


PressSearch = (term) => {
   if (this.state.selectedLabel==''){
    alert('Please Fill The label')
  }
  else if (this.state.text1 == ''){
    alert('Please Fill Text one')
  } else if (this.state.text2 == ''){
    alert('Please Fill Text Two')
  } else {
this.searchYT(term);
}
}

searchYT = term => {
this.setState({loading:true});
YTSearch({key:API_KEY,term}, videos => {
// alert(videos);
console.log(videos[0].id.videoId);
this.setState({loading:false,
  videos,
});
})};

  render(){
    const {text1,text2,selectedLabel} = this.state;
    const {loading,videos} = this.state;
  return (
    <View style={styles.container}>
       <AppHeader />
      <Menu selectedLabel = {this.state.selectedLabel} Show = {this.Show}/> 
      <TextComponent OnchangeTextOne = {this.changeTextOne} OnchangeTexttwo = {this.changeTextTwo} text1 = {this.state.text1} 
      text2 = {this.state.text2}/>
      <StatusBar style="auto" />
      <View>
        <SearchBar loading = {loading}
        text1 = {this.state.text1} text2 = {this.state.text2} selectedLabel = {this.state.selectedLabel} 
        onPressSearch = {this.PressSearch}/>
      </View>
      <ReactYouTube />
      {/* <VideoList videos = {videos}/> */}
    </View>

  );
}
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#ddd',
    // alignItems: 'center',
    // justifyContent: 'center',
    marginVertical:50,
  },


});

export default App;
